// Déclarations pour les fichiers image
declare module "*.webp" {
    const value: string;
    export default value;
  }
  
  declare module "*.jpg" {
    const value: string;
    export default value;
  }
  
  declare module "*.jpeg" {
    const value: string;
    export default value;
  }
  
  declare module "*.png" {
    const value: string;
    export default value;
  }
  
  // Déclaration pour les fichiers JSON
  declare module "*.json" {
    const value: any;
    export default value;
  }
  